// Debugging an SDF, visualize its discontinuity

// orange-blue: SDF isosurfaces
// red-black: discontinuity (high numerical gradient)
// green-pink: surface gradient lower/higher than 1

#define PI 3.1415926


// a debugging SDF, heavily inspired by iq's Snail
float mapShell(in vec3 p0) {
    p0 -= vec3(1.3, 0, 0);
    vec3 p = p0;

    const float b = 0.17;

    float r = length(p.xy);
    float a = mix(0.0, 0.8, smoothstep(0.0, 1.0, 0.5*(r-0.6)));
    p.xy = mat2(cos(a),-sin(a),sin(a),cos(a))*p.xy;
    float t = atan(p.y, p.x);

    float n = (log(r)/b-t)/(2.0*PI);
    n = min(n, 0.0);

    float n0 = floor(n), n1 = ceil(n);
    float x0 = exp(b*(t+2.0*PI*n0));
    float x1 = exp(b*(t+2.0*PI*n1));
    float r0 = 1.0*x0;
    float r1 = 1.0*x1;

    float h0 = p.z + 0.4*(x0-1.0);
    float h1 = p.z + 0.4*(x1-1.0);
    float d0 = length(vec2(x0-r,h0)) - r0;
    float d1 = length(vec2(x1-r,h1)) - r1;

    float d, dx, dy;
    if (d0 < 0.0) d = d0, dx = x0-r, dy = h0;
    else if (d1 < 0.0 && d1<-d0) d = -d0, dx = x0-r, dy = h0;
    else if (d1 < 0.0) d = d1, dx = x1-r, dy = h1;
    else if (d0 < d1) d = d0, dx = x0-r, dy = h0;
    else d = d1, dx = x1-r, dy = h1;

    d += 0.002*r*sin(40.*t);
    d += 0.002*r*sin(40.*atan(dy,dx));

    d = abs(d)-0.1*r;
    d = max(d, p0.x);
    return d;
}

// test SDF
float mapTest(vec3 p) {
    vec3 r = vec3(1.5,1.0,0.6);
    float k1 = length(p/r);
    float k2 = length(p/(r*r));
    return k1*(k1-1.0)/k2+0.1*sign(p.x+p.y+p.z);
}

float sdf(in vec3 p) {
    //return mapTest(p);
    return mapShell(p);
}

vec3 sdfGrad(in vec3 p, in float e) {
	float a = sdf(p+vec3(e,e,e));
	float b = sdf(p+vec3(e,-e,-e));
	float c = sdf(p+vec3(-e,e,-e));
	float d = sdf(p+vec3(-e,-e,e));
	return (.25/e)*vec3(a+b-c-d,a-b+c-d,a-b-c+d);
}



// raymarching parameters
const vec3 BoxRadius = vec3(2.0, 2.0, 2.0);
#define STEP 0.01
#define MAX_STEP 120.


// rendering parameters
#define FIELD_EMISSION 0.3
#define DISCONTINUITY_OPACITY 0.1
#define SURFACE_GRADIENT 10.0

// light direction as global variable
vec3 light = normalize(vec3(0.5,0.5,1.0));

// colormaps - https://www.shadertoy.com/view/NsSSRK
vec3 colorSdf(float t) {
  float r = .385+.619*t+.238*cos(4.903*t-2.61);
  float g = -5.491+.959*t+6.089*cos(.968*t-.329);
  float b = 1.107-.734*t+.172*cos(6.07*t-2.741);
  return clamp(vec3(r, g, b), 0.0, 1.0);
}
vec3 colorNormal(float t) {
  float r = .529-.054*t+.55*cos(5.498*t+2.779);
  float g = .21+.512*t+.622*cos(4.817*t-1.552);
  float b = .602-.212*t+.569*cos(5.266*t+2.861);
  return clamp(vec3(r, g, b), 0.0, 1.0);
}


// modified from a volume rendering demo
// https://github.com/harry7557558/Graphics/blob/master/raytracing/webgl_volume/fs-source.glsl
vec3 render(in vec3 ro, in vec3 rd, float t0, float t1) {
    float step_count = min(ceil((t1-t0)/STEP), MAX_STEP);
    float t = t0, dt = (t1-t0) / step_count;
    vec3 totcol = vec3(0.0);
    float totabs = 1.0;
    float v_old, v;
    for (t = t0; t < t1; t += dt) {
        v = sdf(ro+rd*t);
        vec3 col = colorSdf(0.5+0.5*sin(8.0*PI*v));
        //float grad = length(sdfGrad(ro+rd*t,dt));
        float grad = t==t0 ? 0.0 : abs(v-v_old)/dt;
        float grad_abs = (1.0-grad)/dt;
        col = mix(vec3(1,0,0), col, clamp(exp(grad_abs),0.0,1.0));
        float absorb = FIELD_EMISSION+DISCONTINUITY_OPACITY*max(-grad_abs,0.0);
        totabs *= exp(-absorb*dt);
        totcol += col*absorb*totabs*dt;
        if (v < 0.0) break;
        v_old = v;
    }
    if (v > 0.0) return totcol;
    for (int s = 0; s < 4; s += 1) {
        v_old = v;
        dt *= -0.5;
        for (int i = 0; i < 2; i++) {
            t += dt;
            v = sdf(ro+rd*t);
            if (v*v_old<0.0) break;
        }
    }
    vec3 grad = sdfGrad(ro+rd*t, 1e-3);
    vec3 col = colorNormal(0.5+0.5*tanh(SURFACE_GRADIENT*(0.5*length(grad)-0.5)));
    col = 0.2+0.05*grad.y+col*max(dot(normalize(grad), light),0.0);
    return totcol + col * totabs;
}


// ray intersection with a box
bool boxIntersection(vec3 ro, vec3 rd, out float tn, out float tf) {
    vec3 inv_rd = 1.0 / rd;
    vec3 n = inv_rd*(ro);
    vec3 k = abs(inv_rd)*BoxRadius;
    vec3 t1 = -n - k, t2 = -n + k;
    tn = max(max(t1.x, t1.y), t1.z);
    tf = min(min(t2.x, t2.y), t2.z);
    if (tn > tf) return false;
    return true;
}

// main
void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    // set camera
    float rx = iMouse.z>0. ? 3.14*(iMouse.y/iResolution.y)-1.57 : 0.3;
    float rz = iMouse.z>0. ? -iMouse.x/iResolution.x*4.0*3.14 : -0.6;

    vec3 w = vec3(cos(rx)*vec2(cos(rz),sin(rz)), sin(rx));
    vec3 u = vec3(-sin(rz),cos(rz),0);
    vec3 v = cross(w,u);

    vec3 ro = 10.0*w;
    vec2 uv = 2.0*fragCoord.xy/iResolution.xy - vec2(1.0);
    vec3 rd = mat3(u,v,-w)*vec3(uv*iResolution.xy, 2.0*length(iResolution.xy));
    rd = normalize(rd);

    // calculate pixel color
    light = normalize(w+0.5*u+0.1*v);

    float t0, t1;
    if (!boxIntersection(ro, rd, t0, t1)) {
        fragColor = vec4(vec3(0.0), 1.0);
        return;
    }
    vec3 col = render(ro, rd, t0, t1);;
    fragColor = vec4(col, 1.0);
}

#define Time (iTime-1.0)

const vec3 light_pos = 5.0*vec3(0.7,-2.0,1.8);

#define eps 0.001

vec3 castRay(vec3 p, vec3 d) {
    float t = 0.0, dt, mt=1e12;
	for (int i = min(iFrame,0); i < 256; i++) {
		dt = map(p + t * d, Time);
		t += dt;
        if (dt<-eps) return vec3(1,0,0);
		if (dt<eps) break;
		if (t>20.0) return vec3(0.0);
	}
	p = p + t * d;
    vec3 n = calcGrad(p, Time);
    d-=2.0*dot(d,n)*n;
	vec3 light = normalize(light_pos-p);
	vec3 dif = vec3(0.7, 0.65, 0.6) * max(dot(n, light), 0.0);
	vec3 spc = vec3(0.2) * pow(max(dot(d, light), 0.0), 5.0);
  #if 1
    t=0.1, d=light;
	for (int i = min(iFrame,0); i < 64; i++) {
		dt = map(p + t * d, Time);
		t += dt;
        mt=min(mt,0.5+5.0*dt/t);
        if (t>10.0) {dif*=clamp(mt,0.0,1.0);break;}
        if (dt<0.01) {dif*=0.5;break;}
	}
  #endif
    float m = exp(-0.1*dot(p.xy,p.xy));
    m*=1.0-exp(-2.0*iTime);
    m*=1.0-exp(2.0*(iTime-39.));
    return m*(dif+spc);
}


#define AA 1
#define SC 2.0

float sstep(float x) {
    x=clamp(x,0.,1.);
    return x*x*(3.-2.*x);
}

#define res iResolution.xy
vec3 Ctr = vec3(0,0.5,0.2);

vec3 CamP, ScrO, ScrA, ScrB;
void setCamera(float rz, float rx, float dist, float unit){
    float cx=cos(rx), sx=sin(rx), cz=cos(rz), sz=sin(rz);
    vec3 u=vec3(-sz,cz,0.), v=vec3(-cz*sx,-sz*sx,cx), w=vec3(cz*cx,sz*cx,sx);
    u*=0.5*res.x/unit, v*=0.5*res.y/unit, w*=dist;
    CamP=Ctr+w;
    ScrO=Ctr-(u+v), ScrA=u*2.0,ScrB=v*2.0;
}

void mainImage( out vec4 color, in vec2 coord ){
#if 0
    vec2 p = iMouse.w>0.?iMouse.xy:-iMouse.zw;
    float t = 2.0*PI*(p.x/res.x-0.5);
    float a = PI*(p.y/res.y-0.5);
#else
    float t = 0.3, a=0.2, c;
    if (iTime>6.0) c=sstep(2.0*(iTime-6.)),t=mix(t,-0.1,c),a=mix(a,-0.6,c),Ctr=mix(Ctr,vec3(0,0.5,0.4),c);
    if (iTime>19.0) c=sstep(0.8*(iTime-19.)),a=mix(a,-0.4,c),Ctr=mix(Ctr,vec3(0,0.5,0.6),c);
    if (iTime>26.0) c=sstep(2.0*(iTime-26.)),t=mix(t,0.2,c),a=mix(a,0.3,c),Ctr=mix(Ctr,vec3(0,0.2,0.6),c);
    if (iTime>30.0) c=sstep(2.0*(iTime-30.)),t=mix(t,-0.2,c),a=mix(a,-0.2,c),Ctr=mix(Ctr,vec3(0,0.2,0.2),c);
    if (iTime>36.0) c=sstep(2.0*(iTime-36.)),t=mix(t,0.1,c),a=mix(a,-0.7,c),Ctr=mix(Ctr,vec3(0,0.2,-0.3),c);
#endif
    float CamSC = length(res);
    setCamera(-1.5+t,0.8+a,6000./CamSC,0.2*CamSC);

    vec3 col=vec3(0.0);
    for (int i=0;i<AA;i++) for (int j=0;j<AA;j++) {
        vec2 uv = (coord+vec2(i,j)/float(AA))/res;
        vec3 d = normalize(ScrO+uv.x*ScrA+uv.y*ScrB - CamP);
        col += castRay(CamP,d);
    }
    col/=float(AA*AA);

    //col.x=pow(col.x,0.75),col.y=pow(col.y,0.75),col.z=pow(col.z,0.75);

    color = vec4(col,1.0);
}

#define PI 3.14159265358979

// all sdf functions copy-pasted from iq ;)

float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}
float sdEllipsoid( vec3 p, vec3 r ) {
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}
float sdCylinder(vec3 p, vec3 a, vec3 b, float r) {
  vec3  ba = b - a;
  vec3  pa = p - a;
  float baba = dot(ba,ba);
  float paba = dot(pa,ba);
  float x = length(pa*baba-ba*paba) - r*baba;
  float y = abs(paba-baba*0.5)-baba*0.5;
  float x2 = x*x;
  float y2 = y*y*baba;
  float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));
  return sign(d)*sqrt(abs(d))/baba;
}
float sdTorus( vec3 p, vec2 t ){
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}
float smax( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h);
}

mat3 axisAngle(vec3 a, float t) {  // actually returns its inverse/transpose
	a = normalize(a); float ct = cos(t), st = sin(t);
	return mat3(
		ct + a.x*a.x*(1.-ct), a.x*a.y*(1.-ct) - a.z*st, a.x*a.z*(1.-ct) + a.y*st,
		a.y*a.x*(1.-ct) + a.z*st, ct + a.y*a.y*(1.-ct), a.y*a.z*(1.-ct) - a.x*st,
		a.z*a.x*(1.-ct) - a.y*st, a.z*a.y*(1.-ct) + a.x*st, ct + a.z*a.z*(1.-ct)
	);
}


// ===================================== Animate =====================================

vec3 animatePath(float t){
    t*=4.0;
    float d = 0.57*t;
    float rx=1.0-exp(-0.4*t), ry=1.0-exp(-0.2*t), h=1.0-exp(-3.*t);
    vec3 q = vec3(cos(t+d),sin(1.3*t+d),sin(2.*t));
    q*=vec3(rx,ry,h)*vec3(0.2,0.2,0.2);
    return q;
}

// ===================================== Archer =====================================

// unlisted initial modeling: https://www.shadertoy.com/view/Wdsfzj

vec3 MapC = vec3(0.0,0.5,0.6);

float mapArch(vec3 p){
    //vec3 q = axisAngle(vec3(0,-1,0),0.15)*axisAngle(vec3(1,0,0),0.2)*(p-vec3(0,0,0.97));
    vec3 q=mat3(0.988771,0,-0.149438,-0.0296888,0.980067,-0.196438,0.146459,0.198669,0.969061)*(p-vec3(0,0,0.97));
    float c = sdEllipsoid(q,vec3(0.3,1.0,1.0-0.1*p.z));
    q = p-vec3(0,-16,-0.4);
	float pz2 = (p.z-1.)*(p.z-1.);
    c = smax(c,sdEllipsoid(q,vec3(2.0,15.8,16.0)),smax(0.05*(1.0-pz2),0.0,0.01));
    float h = sdCapsule(p,vec3(0.05,-0.3,0.97),vec3(0.2+exp(0.15*p.y)-1.0,0.35,0.9),0.25+0.08*p.y-pz2);
    float s = length(p-vec3(0.27,0.35,1.0))-0.25;
    float v = sdCylinder(p,vec3(0.35,0.3,0.9),vec3(0.55,0.3,0.9),0.55*(p.y-0.1)+0.2*(p.x-0.3));
    h=smin(h,v,0.1);
    return smin(c,h,0.1);
}
float mapArm(vec3 p){
    //vec3 q = axisAngle(vec3(0,0,1),-0.3)*axisAngle(vec3(0,-1,0),0.1)*axisAngle(vec3(1,0,0),0.1)*(p-vec3(0.43,-0.37,1.0));
    vec3 q = mat3(0.950564,0.294044,-0.0998334,-0.303565,0.947618,-0.0993347,0.0653952,0.12473,0.990033)*(p-vec3(0.43,-0.37,1.0));
    return sdEllipsoid(q,vec3(0.05,0.11,0.25));
}
float mapBody(vec3 p){
    float b = length(p.xy-vec2(-0.1,0.4-0.1*p.z))-0.21+0.01*p.z;
    b = smax(b,abs(p.z-0.5)-0.5,0.05);
    float c = sdEllipsoid(p-vec3(-0.1,0.35,0.9),vec3(0.35,0.4,0.35));
    c = smin(b,c,0.2);
    float u = length(p.xy-vec2(-0.1,0.47))-max(0.23-p.x*p.x,0.);
    u = smax(u,abs(p.z-0.1*p.y-1.35)-0.35,max(0.1*(2.0-p.z),0.));
    c = smin(u,c,0.2);
    return c;
}
float archerTotal(vec3 p, float t) {
	float a = mapArch(p);
	float b = mapBody(p);
	float sd = smin(a, b, 0.2);
	float am = mapArm(p);
	sd = smin(sd, am, 0.05);
	return mix(sd, length(p-vec3(0.0,0.0,0.8))-0.8, t);
}

vec3 archerPath(float t){
    t-=4.7; if (t<0.) return vec3(0,0,1e12);
    t=max(t-0.3,0.);
    float h = 0.2+exp(-t*t)/t;
    vec3 p= animatePath(t-0.1)+vec3(0.0,0.5,h);
    float m=sin(5.*t); m=m*m;
    p.z=mix(p.z,m,clamp(0.5*(t-13.),0.,1.))+0.07;
    p+=max(pow(12.*(t-16.9),3.),0.)*vec3(0,-1,1.5);
    return p;
}
mat3 archerOrientation(float t){
    t-=0.1;
    float rx=clamp(t-5.0,0.0,0.2);
    rx+=.7*(0.5-1./(t-3.))*sin(2.*t);
    rx+=max(30.*(t-21.9),0.);
    vec3 i=normalize(vec3(0,-1,0.3-rx)), j=normalize(vec3(1,0,0)), k=cross(i,j);
    return transpose(mat3(i,j,k));
}

float archMeta(float t){
    return clamp(2.0*(8.5-t),0.,1.);
}

float mapArcher(vec3 p, float t){
    const float SC = 0.6;
    mat3 R=archerOrientation(t)*(1./SC);
    p=R*(p-archerPath(t));
    return SC*archerTotal(p,archMeta(t));
}


// ===================================== Hand =====================================

vec3 handPath(float t){
    t-=4.7;
    if (t<0.) return vec3(0,0,t*t*t);
    t=max(t-0.3,0.);
    vec3 p= animatePath(t);
    p*=max(1.-exp(3.*(t-15.)),0.);
    p+=max(pow(8.*(t-16.9),3.),0.)*vec3(0,-1,0.8);
    if (t>18.0) p=vec3(0,0,0.5*pow(21.5-t,3.));
    return p;
}
mat3 handOrientation(vec3 p, float t){
    float rx=clamp(t-5.0,0.0,0.2);
    rx+=.5*(0.5-1./(t-3.))*sin(2.*t);
    rx*=max(1.-exp(5.*(t-20.5)),0.);
    rx+=max(20.*(t-21.9),0.);
    if (t>23.0) rx=0.1*sin(t);
    vec3 i=normalize(vec3(1,0,0)), j=normalize(vec3(0,1,rx)), k=cross(i,j);
    return (mat3(i,j,k));
}

float mapHand(vec3 p, float t){
    mat3 M = handOrientation(p,t);
    vec3 A0 = M*vec3(-0.2,-0.5,0.08);
    vec3 A1 = M*vec3(-0.5,-0.18,-0.01);
    vec3 A2 = M*vec3(-0.68,0.15,0.03);
    vec3 A3 = M*vec3(-0.98,0.3,0.03);
    vec3 B1 = M*vec3(-0.26,0.31,-0.05);
    vec3 B2 = M*vec3(-0.36,0.65,-0.14);
    vec3 B3 = M*vec3(-0.42,0.86,-0.15);
    vec3 B4 = M*vec3(-0.50,1.12,-0.08);
    vec3 C1 = M*vec3(-0.04,0.28,-0.08);
    vec3 C2 = M*vec3(0,0.74,-0.23);
    vec3 C3 = M*vec3(0,1,-0.16);
    vec3 C4 = M*vec3(0,1.25,-0.08);
    vec3 D1 = M*vec3(0.17,0.2,-0.08);
    vec3 D2 = M*vec3(0.3,0.65,-0.17);
    vec3 D3 = M*vec3(0.34,0.84,-0.12);
    vec3 D4 = M*vec3(0.41,1.09,0);
    vec3 E0 = M*vec3(0.23,-0.5,0.1);
    vec3 E1 = M*vec3(0.39,0.10,-0.06);
    vec3 E2 = M*vec3(0.53,0.36,-0.09);
    vec3 E3 = M*vec3(0.62,0.51,-0.04);
    vec3 E4 = M*vec3(0.68,0.75,0.09);
    float r = 0.1;

    float d = 1e12;
    p-=handPath(t);
    #define CS(a,b) d=smin(d,sdCapsule(p,a,b,r),0.05)
	CS(A0,A1); CS(A1,A2); CS(A2,A3);
	CS(B1,B2); CS(B2,B3); CS(B3,B4);
	CS(C1,C2); CS(C2,C3); CS(C3,C4);
	CS(D1,D2); CS(D2,D3); CS(D3,D4);
	CS(E0,E1); CS(E1,E2); CS(E2,E3); CS(E3,E4);
    #undef CS
    return d;

}


// ===================================== Ring =====================================

const float Ground_Z = -1.0;

#define ringR 0.15
#define ringr 0.04

vec3 ringPath(float t){
    float t0=t;
    const vec3 g = vec3(0,0,-9.8);
    vec3 p=vec3(0.8,1.2,2.0), v=vec3(-0.5,-0.2,0.0);
    float mt=0.0;
    for (int i=min(int(t),0);i<10;i++){
        mt=(-sqrt(v.z*v.z-2.0*g.z*(p.z-Ground_Z-ringR-ringr))-v.z)/g.z;
        if (mt>t){
            return p+t*(v+0.5*g*t);
        }
        t-=mt, p=p+mt*(v+0.5*g*mt);
        v=0.5*(v+g*mt); v.z=-v.z;
        p+=1e-4*v;
    }
    t=t0-2.5;
    if (t<0.) return p;
    if (t<.5) return p+0.5*t*t*g;
    if (t0<23.) return handPath(t0)+handOrientation(vec3(0.0),t0)*vec3(0.3,0.6,-0.17);
    p=vec3(0.5,0.7,5.0), v=vec3(0,0,-1), mt=0.0,t=t0-29.0;
    if (t<0.) return vec3(0,0,1e12);
    for (int i=min(int(t),0);i<10;i++){
        mt=(-sqrt(v.z*v.z-2.0*g.z*(p.z-Ground_Z-ringR-ringr))-v.z)/g.z;
        if (mt>t){
            return p+t*(v+0.5*g*t);
        }
        t-=mt, p=p+mt*(v+0.5*g*mt);
        v=0.5*(v+g*mt); v.z=-v.z;
        p+=1e-4*v;
    }
    if (t0<33.5) return p;
    return vec3(p.xy,max(p.z-0.5*(t0-33.5),Ground_Z+ringr));
}
mat3 ringOrientation(float t){
    if (t<3.0) return mat3(1.0);
    vec3 i=normalize(vec3(1.0,-0.3,0.0)),k=vec3(0,0,1),j=cross(k,i);
    if (t<23.) transpose(handOrientation(vec3(0.0),t)*mat3(i,j,k));
    t-=33.5;
    if (t<0.) return mat3(1.0);
    float a=clamp(5.*t,0.,.5*PI);
    return mat3(1,0,0,0,cos(a),sin(a),0,-sin(a),cos(a));
}

float mapRing(vec3 p, float t){
    p = ringOrientation(t) * (p - ringPath(t));
    return sdTorus(p,vec2(ringR,ringr));
}

float ringsm(float t){
    if (t<2.5) return 0.0;
    if (t<3.0) return t-2.5;
    if (t>25.0) return 0.0;
    return 0.1;
}


// ===================================== Together =====================================


float map(vec3 p, float t){
    float a=1e12,h=1e12,r=1e12,g=1e12;
    a = 0.5*mapArcher(p,t);
    h = mapHand(p,t);
    r = mapRing(p,t);
    g = p.z-Ground_Z;
    if (t<0.) return g;
    return min(a,min(smin(h,g,0.1),smin(r,g,ringsm(t))));
}


vec3 calcGrad(vec3 p, float t){
    const float e = .001;
#if 0
	float a = map(p+vec3(e,e,e),t);
	float b = map(p+vec3(e,-e,-e),t);
	float c = map(p+vec3(-e,e,-e),t);
	float d = map(p+vec3(-e,-e,e),t);
	return normalize(vec3(a+b-c-d,a-b+c-d,a-b-c+d));
#else
    vec3 n = vec3(0.0);
    for(int i=min(int(t),0); i<4; i++){
        vec3 h = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += h*map(p+h*e,t);
    }
    return normalize(n);
#endif
}

