// Comparing two ellipse distance methods found on the internet
// The solution that solves a quartic equation is in Common
// The iteration method works ~1.2 times faster than the analytical one
// Step length may need to be reduced when using the iteration method in raymarching



// This iteration method is introduced in
// https://wet-robots.ghost.io/simple-method-for-distance-to-ellipse/

// Line 23: the asin() is removed to increase performance

float sdEllipse_iter(vec2 p, vec2 ab){
    p = abs(p);
    float t = 0.785398;
    vec2 xy;
    for (int i=0;i<3;i++){
        vec2 cs = vec2(cos(t),sin(t));
        xy = ab*cs;
        vec2 e = (ab.x*ab.x-ab.y*ab.y)*vec2(1,-1)*cs*cs*cs/ab;
        vec2 r = xy-e, q = p-e;
        //float rm = length(r), qm = length(q);
        //float dc = rm*asin((r.x*q.y-r.y*q.x)/(rm*qm));
        float dc = (r.x*q.y-r.y*q.x)/length(q);
        float dt = dc/sqrt(dot(ab,ab)-dot(xy,xy));
        t += dt;
        t = min(1.570796,max(0.,t));
    }
    vec2 q = p/ab;
    return sign(dot(q,q)-1.)*length(p-xy);
}




