// linear and quadratic regression
#define LinearReg float m=1.0/(s*sx2-sx*sx); a=m*(s*sxy-sx*sy), b=m*(sx2*sy-sx*sxy);
#define QuadReg float r0=sx2*s-sx*sx,r1=sx3*s-sx*sx2,r2=sx3*sx-sx2*sx2,r21=sx4*sx-sx2*sx3,m=1.0/(sx4*r0-sx3*r1+sx2*r2); a=(sx2y*r0-sxy*r1+sy*r2)*m, b=-(sx2y*r1-sxy*(sx4*s-sx2*sx2)+sy*r21)*m, c=(sx2y*r2-sxy*r21+sy*(sx4*sx2-sx3*sx3))*m;

//#define Z min(0,iFrame)
#define Z 0

// Method 1: y = exp(ax+b), minimum Σ [ln(y)-(ax+b)]²
void fitLinear(in int[N] Y, out float a, out float b){
	float s = float(N), sx = .5*s*(s-1.), sx2 = sx*(2.*s-1.)/3.,
        sxy = 0., sy = 0.;
	for (int i = Z; i < N; i++) {
		float y = log(float(Y[i]));
		sy += y, sxy += float(i)*y;
	}
	LinearReg
}

// Method 2: y = exp(ax+b), minimum Σ y·[ln(y)-(ax+b)]²
void fitLinear_S(in int[N] Y, out float a, out float b){
	float s = 0., sx = 0., sx2 = 0., sxy = 0., sy = 0.;
	for (int i = Z; i < N; i++) {
		float x = float(i), ey = float(Y[i]), y = log(ey);
		sx2 += x*x*ey, sx += x*ey, s += ey, sxy += x*y*ey, sy += y*ey;
	}
	LinearReg
}

// Method 3: y = exp(ax²+bx+c), minimum Σ [ln(y)-(ax²+bx+c)]²
void fitQuadratic(in int[N] Y, out float a, out float b, out float c){
	float s=float(N), sx=.5*s*(s-1.), sx2=sx*(2.*s-1.)/3., sx3=sx*sx, sx4=.2*sx2*(s*(s-1.)*3.-1.),
        sx2y = 0., sxy = 0., sy = 0.;
	for (int i = Z; i < N; i++) {
		float x = float(i), y = log(float(Y[i]));
		sx2y += x*x*y, sxy += x*y, sy += y;
	}
    QuadReg
}

// Method 4: y = exp(ax²+bx+c), minimum Σ y·[ln(y)-(ax²+bx+c)]²
void fitQuadratic_S(in int[N] Y, out float a, out float b, out float c){
	float s = 0., sx = 0., sx2 = 0., sx3 = 0., sx4 = 0., sx2y = 0., sxy = 0., sy = 0.;
	for (int i = Z; i < N; i++) {
		float x = float(i), e = float(Y[i]), y = log(e), x2 = x*x;
		sx4 += x2*x2*e, sx3 += x2*x*e, sx2 += x2*e, sx += x*e, s += e;
		sx2y += x2*y*e, sxy += x*y*e, sy += y*e;
	}
    QuadReg
}

