/*
	Fitting an unit arc using cubic bezier curve
	Ellipse fitting can be done by simply applying a linear transform.

	Basic Idea:

	Let the start point be A(0,1) and the endpoint be B(cosθ,sinθ), the two other 
	control points be C(1,x) and D=B+x*(sinθ,-cosθ). Let the equation of the bezier 
	curve be P(t)=(1-t)³·A+3t(1-t)²·B+3t²(1-t)·C+t³·D and the error be 
	E(x)=Integral[(P²-1)²,t,0,1]. This integral is be a quartic polynomial 
	about x. Let its derivative equal to zero and solve for x, thus determine 
	control point B and C.

	Since the calculation is very machanical and error-prone, I used WolframAlpha 
	to do expanding, integration and differentiation.

	As one can see, when θ<2rad, it's not easy to notice the difference between the 
	circle and the fitted curve.

	2020-03-05:
	Add numerical approximation solutions generated by polynomial regression.
	(Simpson's integration in [0,2], 2×100000 samples, Gaussian elimination, double-precision)
*/


#define PI 3.1415926
#define _13 0.333333333
#define cbrt(x) ((x)>0.?pow(x,_13):-pow(-(x),_13))

#define Clamp(x) clamp(x,0.,1.)

struct Bezier3{
    vec2 A, B, C, D;
};


// ========================= Fitting Code =========================


void fitArc(float a, out Bezier3 R) {
    float S = sin(a), C = cos(a), x;
#if 1
    // derivative of error: error'=ax³+bx²+cx+d
	float s2 = S * S, c2 = C * C, sc2 = s2 + c2, sc22 = sc2 * sc2;
	a = 1. / (756.*(sc22 + 1.) + 810.*s2 - 1890.*(sc2 + 1.)*C + 2430.*c2);
	float c = (2520.*sc22 + 2736.*s2 + (-507.*sc2 - 6600.*C + 7215.)*C - 2628.) * a,
		b = (3996.*(sc2 + 1.) - 6750.*C)*S * a, d = (3439.*sc2 + 4276.*C - 7715.)*S * a;
    // solve the cubic equation to determine the minima
	float p = (c - b * b * _13) * _13, q = -0.5 * ((b*b / 13.5 - c * _13) * b + d);
	a = q * q + p * p * p;
	x = a > 0.0 ? cbrt(q + sqrt(a)) + cbrt(q - sqrt(a)) - b * _13
		: 2.0 * pow(q*q - a, 0.5*_13) * cos(atan(sqrt(-a), q) * _13) - b * _13;
    // 450+ time unit
#else
    // numerical approximation
    // 12 time unit, ERR = 2.75e-04, RMSE = 8.02e-05, notable error
    x = ((0.008561080643*a-0.002518989170)*a+0.334292025655)*a;
    // 23 time unit, ERR = 2.51e-06, RMSE = 6.29e-07, few error
    x = ((((0.000223623140*a-0.000237833794)*a+0.007216725971)*a-0.000130686154)*a+0.333353941704)*a;
    // 28 time unit, ERR = 2.71e-07, RMSE = 6.11e-08, no visible error
    x = (((((0.000027936409*a+0.000060660755)*a+0.000117720501)*a+0.006861171676)*a+0.000027337977)*a+0.333330238084)*a;
#endif
    // apply the solution of the equation to the control points
    R.A=vec2(1,0), R.B=vec2(1,x), R.C=vec2(C+x*S,S-x*C), R.D=vec2(C,S);
}

float sdBezier3(in Bezier3 R, in vec2 p) {
	vec2 A = R.D-R.A+3.*(R.B-R.C), B = 3.*(R.C-2.*R.B+R.A), C = 3.*(R.B-R.A);
	vec2 a=vec2(1,0), b, pa=p-a, ba, q;
	float d = dot(pa,pa);
	for (float dt=.02, t = dt; t < 1.; t += dt) {
		b = ((A*t + B)*t + C)*t + R.A;
		pa=p-a, ba=b-a, q=pa-ba*Clamp(dot(pa,ba)/dot(ba,ba));
		d = min(d, dot(q,q));
		a = b;
	}
	return sqrt(d);
}

void mainImage(out vec4 col, in vec2 P) {
    vec2 res = iResolution.xy;
    vec2 p = 6.0*(P-.5*res)/length(res);
    float h = .2*length(res); 	// hardness of anti-aliasing

    float ang = atan(p.y,p.x), a;
    if (iMouse.z>0.){
        vec2 d = iMouse.xy-.5*iResolution.xy;
        a = atan(d.y,d.x);
    }
    else a = sin(iTime), a=PI*a*a;
    if (ang<0.) ang+=2.*PI; if (a<0.) a+=2.*PI;

    // axis
    vec2 axis = Clamp(h*(abs(p)-.02));
    vec3 bkg = mix(vec3(0,0,1),vec3(1),min(axis.x,axis.y));

    // circle
    float circ = Clamp(h*(abs(length(p)-1.)-.02));
    vec3 c = mix(ang<a?vec3(1,0,0):vec3(.7),bkg,circ);

    // bezier curve
    Bezier3 R;
    fitArc(a, R);
    float d = sdBezier3(R,p);
    c = mix(vec3(0),c,Clamp(h*(d-.01)));

    // vertexes
    d = min(length(p-R.B),length(p-R.C))-.04;
    c = mix(vec3(0),mix(vec3(1),c,Clamp(h*d)),Clamp(h*(abs(d)-.002)));
    d = min(length(p-R.A),length(p-R.D))-.05;
    c = mix(vec3(0),mix(vec3(1),c,Clamp(h*d)),Clamp(h*(abs(d)-.005)));

    col = vec4(c,1.0);
}

