#define PI 3.14159265358979

float opExtrusion(float sd, float h, vec3 P) {
    if (P.z >= 0. && P.z <= h) return sd > 0. ? sd : max(sd, max(-P.z, P.z - h));
	if (P.z > h) return sd > 0. ? sqrt(sd*sd + (P.z - h)*(P.z - h)) : P.z - h;
	if (P.z < 0.) return sd > 0. ? sqrt(sd*sd + P.z*P.z) : -P.z;
}

float sdCylinder(float r, float min_z, float max_z, vec3 p) {
    p.z-=min_z, max_z-=min_z, min_z=0.0;
    float d=length(p.xy)-r;
    if (p.z>0.0 && p.z<max_z) return max(d, max(-p.z, p.z-max_z));
    if (p.z>max_z) p.z-=max_z;
    else p.z=-p.z;
    return d<0.0?p.z:sqrt(d*d+p.z*p.z);
}

float sdPolygon(vec2 v[4], vec2 p) {
	float sd = dot(p - v[0], p - v[0]);
	float sgn = 1.0;
	vec2 e, w, b; float c;
	for (int i = 0; i < 4; i++) {
		e = v[(i+1)%4] - v[i];
		w = p - v[i];
		b = w - e*clamp( dot(w,e)/dot(e,e), 0.0, 1.0 );
		sd = min(sd, dot(b,b));
		if (e.y < 0.0) e.y = -e.y, w.y = -w.y;
		if (w.y > 0.0 && w.y < e.y && (w.y*e.x / e.y > w.x)) sgn =-sgn;
	}
	return sgn*sqrt(sd);
}

float s_sub(float a, float b, float k) {
	float h = 0.5 - 0.5*(b + a) / k;
	if (h < 0.0) return a; if (h > 1.0) return -b;
	return mix(a,-b,h) + k*h*(1.0 - h);
}

float s_min(float a, float b, float k) {
	float h = 0.5 + 0.5*(b - a) / k;
	if (h < 0.0) return b; if (h > 1.0) return a;
	return mix(b,a,h) - k*h*(1.0 - h);
}

float sdCup(vec3 p) {
    p.z-=0.23;
    float cyl = sdCylinder(0.75, 0.0, 2.0, p);
    float sd = abs(cyl - 0.15) - 0.08;
    sd = s_sub(sd, 1.8 - p.z, 0.08);
    vec3 q=p.xzy-vec3(1.03,0.38,-0.15);
    float handle = sdPolygon(vec2[4](vec2(0,0.9),vec2(0.4,0.9),vec2(0.3,0.35),vec2(0,0.1)),q.xy);
    handle=opExtrusion(abs(handle-0.2)-0.05, 0.3, q)-0.1;
    handle=max(handle,0.12-cyl);
    sd = s_min(sd, handle, 0.05);
    return sd;
}

float map(vec3 p) {
    p.z+=1.0;
    return min(p.z, sdCup(p));
}

#define ZERO min(int(time),0)
#define eps 1e-3
#define MAX_STEP 256
#define MAX_DIST 20.0


vec3 light = normalize(vec3(-0.3, 0.1, 1));

vec3 calcCol(in vec2 coord, in vec2 res, in vec4 iMouse, in float time, sampler2D g) {
    vec3 Pos;
    if (iMouse.z>0.){
        float u = 2.0*PI*iMouse.x/res.x;
        float v = 0.5*PI*iMouse.y/res.y;
        Pos = 5.0*vec3(sin(u)*cos(v),cos(u)*cos(v),sin(v));
    }
    else{
    	float H = cos(0.4*time); H=3.5*H*H+0.5;
    	float R = sqrt(20.0-H*H)+0.2*(cos(time)+1.0);
    	Pos=vec3(R*cos(time), R*sin(time), H);
    }

    float rz=atan(-Pos.x,Pos.y), rx=atan(length(Pos.xy),-Pos.z);
    mat3 M=mat3(cos(rz),sin(rz),0,-sin(rz),cos(rz),0,0,0,1)*mat3(1,0,0,0,cos(rx),sin(rx),0,-sin(rx),cos(rx));

    vec3 d = M*normalize(vec3(0.5*res.x-coord.x,-0.5*res.y+coord.y,0.7*length(res)));


	// cast ray
	float t = 100.0 * eps, dt;
	vec3 p = Pos + t * d;
	if (map(p) < 0.0) return vec3(0.0, 0.0, 0.0);
	for (int i = ZERO; i < MAX_STEP; i++) {
		dt = map(Pos + t * d);
		t += dt;
		if (dt < -eps) return vec3(1.0, 0.0, 0.0);
		if (dt < eps) break;
		if (dt > MAX_DIST || i+1 == MAX_STEP) {
            return vec3(0.0);
			//t = max(dot(d, light), 0.0);
			//return vec3(t, t, t);
		}
	}
	p = Pos + t * d;
    vec3 col = vec3(0.87,0.8,0.7);
    if (p.z+1.0<eps) col = texture(g,0.1*p.xy).rgb;
    col *= exp(-0.01*dot(p.xy,p.xy));

	// calculate normal
	float k_111 = map(vec3(p.x + eps, p.y + eps, p.z + eps));
	float k_100 = map(vec3(p.x + eps, p.y - eps, p.z - eps));
	float k_010 = map(vec3(p.x - eps, p.y + eps, p.z - eps));
	float k_001 = map(vec3(p.x - eps, p.y - eps, p.z + eps));
	vec3 n = normalize(vec3(k_111 + k_100 - k_010 - k_001, k_111 - k_100 + k_010 - k_001, k_111 - k_100 - k_010 + k_001));
	if (dot(n, d) > 0.0) n = -n;

	// calculate shadow
	d -= 2.0 * dot(d, n)*n;
	float r = 1.0;
	t = 0.01;
	for (int i = ZERO; i < MAX_STEP; i++) {
		dt = map(p + t * light);
		r = min(r, 15.0 * dt / t);
		if (r < 0.01 || t > 2.0) break;
		t += 0.1*clamp(dt, 0.5, 2.0);
	}
	r = clamp(r, 0.0, 1.0);

	// final result
	float dif = clamp(dot(n, light), 0.0, 1.0);
	return (dif*r + 0.2*pow(max(dot(d, light), 0.0), 4.0))*col + (0.5 - 0.5*dif)*vec3(0.1, 0.15, 0.25);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 col=calcCol(fragCoord+vec2(0.5),iResolution.xy,iMouse,iTime,iChannel0);
    fragColor = vec4(clamp(col,0.0,1.0),1.0);
}

