// get vec4(u, dudx, dudy, 1)
vec4 getVal(sampler2D sampler, vec2 xy) {
    float h = getH(xy);
    float hx1 = getH(xy+nds.xz);
    float hx0 = getH(xy-nds.xz);
    float hy1 = getH(xy+nds.zy);
    float hy0 = getH(xy-nds.zy);
    float hx = (hx1-hx0) / (2.*nds.x);
    float hy = (hy1-hy0) / (2.*nds.y);
    return vec4(h, hx, hy, 1);
}

// ray intersection with a box
bool boxIntersection(vec3 ro, vec3 rd, out float tn, out float tf) {
    vec3 inv_rd = 1. / rd;
    vec3 n = inv_rd*(ro);
    vec3 k = abs(inv_rd)*view_box;
    vec3 t1 = -n - k, t2 = -n + k;
    tn = max(max(t1.x, t1.y), t1.z);
    tf = min(min(t2.x, t2.y), t2.z);
    if (tn > tf) return false;
    return true;
}

// raymarhing, return RGB
vec3 render(vec3 ro, vec3 rd, float t0, float t1) {
    float t = t0;
    for (int i=0; i<64; i++) {
        vec3 p = ro+rd*t;
        vec4 vn = getVal(iChannel0, p.xy);
        float dt = 0.6*(p.z-vn.x) / length(vn.yzw);
        t += dt;
        if (t<=t0 || t>=t1) return vec3(0.1);
        if (abs(dt)<.001) break;
    }
    vec3 n = normalize(getVal(iChannel0, (ro+rd*t).xy).yzw);
    rd = reflect(rd, n);
    return vec3(0.5,0.5,1.0)*(.1+.5*n.z+.5*max(rd.z,0.));
}

