    vec3 w = vec3(cos(rx)*vec2(cos(rz),sin(rz)), sin(rx));
    vec3 u = vec3(-sin(rz),cos(rz),0);
    vec3 v = cross(w,u);
    vec3 ro = scale*perspective*w + view_box-vec3(0,0,0.1);
    vec2 uv = 2.*fragCoord.xy/iResolution.xy - 1.;
    vec3 rd = mat3(u,v,-w)*vec3(uv*iResolution.xy, perspective*iResolution.y);
    rd = normalize(rd);
    float t0, t1;
    if (boxIntersection(ro-view_box, rd, t0, t1)) {
        fragColor.xyz = render(ro, rd, t0, t1);
