#define PI 3.1415927

// pass uniform variables to common
#define uniforms_params vec3 iResolution, vec4 iMouse, int iFrame
#define uniforms_call iResolution, iMouse, iFrame

// simulation dimension in meters
#define height (2.)
#define width (height*(iResolution.x/iResolution.y))
#define view_box (.5*vec3(width,height,1))

// time in seconds (60 fps)
#define delta_t (0.01/60.)
#define time (float(iFrame)*(delta_t))

// sampling buffer
#define getH(xy) (texture(sampler,(xy)/vec2(width,height)).x)
#define getU(xy) (texture(sampler,(xy)/vec2(width,height)).y*((xy).x<=0.||(xy).x>=width?-1.:1.))
#define getV(xy) (texture(sampler,(xy)/vec2(width,height)).z*((xy).y<=0.||(xy).y>=height?-1.:1.))

// numerical differentiation step in meters
#define nds vec3(vec2(width,height)/iResolution.xy,0)

// used in initial condition and mouse interaction, integrates to 0
#define bump(r) (cos(1.8483*(r))*exp(-(r)*(r)))


// outputs (h,u,v), (∂h/∂t,∂u/∂t,∂v/∂t)
// sampler wrap needs to be "clamp"
void diffequ(sampler2D sampler, float t, vec2 xy,
             out vec3 huv, out vec3 huvt,
             uniforms_params) {

    // parameters
    const float h0 = 0.5;  // mean height (m)
    const float g = 9.8;  // acceleration due to gravity (m/s²)
    const float f = .0001;  // Coriolis coefficient (rad/s)
    const float k = 0.1;  // viscous drag for velocity
    const float nu = .001;  // kinematic viscosity (m²/s), 1e-6 in reality

    // height of water surface
    float h = getH(xy);
    float hx1 = getH(xy+nds.xz);
    float hx0 = getH(xy-nds.xz);
    float hy1 = getH(xy+nds.zy);
    float hy0 = getH(xy-nds.zy);
    float hx = (hx1-hx0) / (2.*nds.x);
    float hy = (hy1-hy0) / (2.*nds.y);

    // horizontal velocity
    float u = getU(xy);
    float ux1 = getU(xy+nds.xz);
    float ux0 = getU(xy-nds.xz);
    float uy1 = getU(xy+nds.zy);
    float uy0 = getU(xy-nds.zy);
    float ux = (ux1-ux0) / (2.*nds.x);
    float uy = (uy1-uy0) / (2.*nds.y);
    float ulap = (ux1+ux0-2.*u)/(nds.x*nds.x) + (uy1+uy0-2.*u)/(nds.y*nds.y);

    // vertical velocity
    float v = getV(xy);
    float vx1 = getV(xy+nds.xz);
    float vx0 = getV(xy-nds.xz);
    float vy1 = getV(xy+nds.zy);
    float vy0 = getV(xy-nds.zy);
    float vx = (vx1-vx0) / (2.*nds.x);
    float vy = (vy1-vy0) / (2.*nds.y);
    float vlap = (vx1+vx0-2.*v)/(nds.x*nds.x) + (vy1+vy0-2.*v)/(nds.y*nds.y);

    // https://en.wikipedia.org/wiki/Shallow_water_equations#Non-conservative_form
    float ht = -h*(ux+vy) -u*hx-v*hy;
    float ut = -g*hx -u*ux-v*uy +nu*ulap -k*u+f*v;
    float vt = -g*hy -u*vx-v*vy +nu*vlap -k*v-f*u;

    // mouse interaction
    if (iMouse.z > 0.) {
        float r = 20.*length(xy-vec2(width,height)*iMouse.xy/iResolution.xy);
        h += 8.*bump(r)*delta_t;
    }

    // initial state
    if (iFrame < 1) {
        float r = 20.*length(xy-vec2(.4,.3)*vec2(width,height));
        h = h0 + .2*bump(r);
        u = v = 0.;
        ht = ut = vt = 0.;
    }

    // output
    huv = vec3(h, u, v);
    huvt = vec3(ht, ut, vt);
}

