// scene
vec3 intersectRay(vec3 ro, vec3 rd) {
    vec3 col = mix(vec3(0.0), 0.1*vec3(0.2,0.3,0.8), 0.5+0.5*sin(4.0*rd.x)*cos(4.0*rd.y)*sin(4.0*rd.z));
    vec3 p;
    // red
    p = ro+rd*(-(ro.y+4.0)/rd.y);
    if (length(p.xz-vec2(-0.8,-0.8))<0.2) col+=20.0*vec3(1.0,0.3,0.1);
    // yellow
    p = ro+rd*(-(ro.y+10.0)/rd.y);
    if (length(p.xz-vec2(3.0,2.0))<0.2) col+=20.0*vec3(1.0,0.8,0.5);
    // blue
    p = ro+rd*(-(ro.y+20.0)/rd.y);
    if (length(p.xz-vec2(0.0,-1.5))<0.2) col+=20.0*vec3(0.1,0.6,1.0);
    // green
    p = ro+rd*(-(ro.y+50.0)/rd.y);
    if (length(p.xz-vec2(-12.0,1.0))<0.2) col+=40.0*vec3(0.1,1.0,0.4);
    return col;
}

// quasi-random
float vanDerCorput(float n, float b) {
    float x = 0.0;
    float e = 1.0 / b;
    while (n > 0.0) {
        float d = mod(n, b);
        x += d * e;
        e /= b;
        n = floor(n / b);
    }
    return x;
}

// main
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // random number seed
    vec3 p3 = fract(fragCoord/iResolution.xy*.1031).xyx;
    p3 += dot(p3, p3.zyx + 31.32);
    float h = fract((p3.x + p3.y) * p3.z);
    float seed = floor(65536.*h) + float(iFrame);

    // camera parameters
    const vec3 POS = vec3(3.0,3.0,0.0);
    const float SCALE = 1.5;  // larger = smaller (more view field)
    const float DIST = 0.2;  // larger = smaller
    const float VIEW_FIELD = 0.5;  // larger = larger + more perspective
    const float APERTURE = 0.02;  // larger = blurred

    // sample aperture shape
    float rand1 = vanDerCorput(seed, 2.);
    float rand2 = vanDerCorput(seed, 3.);
    vec2 rnd;

    ivec2 shape_id = ivec2(floor(4.0*fragCoord.xy/iResolution.xy));
    if (shape_id.y==3) {
        if (shape_id.x==0) rnd = randCircle(rand1, rand2);
        if (shape_id.x==1) rnd = randSector(0.8*PI, rand1, rand2);
        if (shape_id.x==2) rnd = randEllipse(1.2, 0.8, rand1, rand2);
        if (shape_id.x==3) rnd = randHeart(rand1, rand2);
    }
    if (shape_id.y==2) {
        if (shape_id.x==0) rnd = randSquare(rand1, rand2);
        if (shape_id.x==1) rnd = randQuad(vec2(-1.0,-1.0), vec2(0.8,-0.9), vec2(1.0,1.2), vec2(-0.4,0.8), rand1, rand2);
        if (shape_id.x==2) rnd = randPolygon(5., rand1, rand2);
        if (shape_id.x==3) rnd = randStar(5., rand1, rand2);
    }
    if (shape_id.y==1) {
        if (shape_id.x==0) rnd = randConcentric(0.6, 1.1, rand1, rand2);
        if (shape_id.x==1) rnd = randIntersection(0.9, 1.6, rand1, rand2);
        if (shape_id.x==2) rnd = randUnion(0.6, 0.8, rand1, rand2);
        if (shape_id.x==3) rnd = randSubtraction(1.0, 1.0, rand1, rand2);
    }
    if (shape_id.y==0) {
        if (shape_id.x==0) rnd = randCosine(0.4, rand1, rand2);
        if (shape_id.x==1) rnd = randCardioid(rand1, rand2);
        if (shape_id.x==2) rnd = randRose(5., rand1, rand2);
        if (shape_id.x==3) rnd = randSpiral(0.2, -0.1, 0.3, rand1, rand2);
    }

    // camera
    vec3 ro = POS+vec3(0,DIST,0);
    vec2 randuv = vec2(vanDerCorput(seed,5.), vanDerCorput(seed,7.));
    vec2 uv = SCALE*(2.0*fract(4.0*(fragCoord.xy+randuv-0.5)/iResolution.xy)-1.0);
    vec2 sc = iResolution.xy/length(iResolution.xy);
    vec2 offset = APERTURE*rnd;
    ro.xz += offset;
    vec3 rd = vec3(VIEW_FIELD*uv*sc+vec2(-0.25,0.0)-offset/DIST, -1.0).xzy;

    // calculate pixel color
    vec3 col = intersectRay(ro, rd);
    vec4 rgbn = texelFetch(iChannel0, ivec2(int(fragCoord.x), int(fragCoord.y)), 0);
    fragColor = vec4((rgbn.xyz*rgbn.w + col)/(rgbn.w+1.0), rgbn.w+1.0);
}

/*
	A new discovery made after a curious attempt - every time we 
	calculate the distance to a quadratic bezier curve by 
	solving a cubic equation, we made an unnecessary calculation.

	The usual way to solve cubic equation has 4 cases. Each case 
	is represented in a different color - orange, purple, blue, 
	and green. But in this demo, no matter how you choose three 
	control points, the green won't appear.

	I get this based on my observation without mathematical prove. 
	If you see green appear, please let me know.

*/

vec3 col;  // color as global variable

#define PI 3.14159265
#define _13 0.33333333
#define PI_3 1.04719755
float SDBezier2(vec2 A, vec2 B, vec2 C, in vec2 P) {
	vec2 C2 = A-2.0*B+C, C1 = 2.0*(B-A), C0 = A-P, E;
	float t = 0.5/dot(C2,C2),
		a = dot(C2,C1)*t, b = (2.0*dot(C2,C0)+dot(C1,C1))*t, c = dot(C1,C0)*t;
	float a2 = 9.0*a*a, p = (-a2*_13+b)*_13, q = 0.11111111*a*a2+0.5*(c-a*b),
		p3 = p*p*p, delta = q*q+p3;
	float sd, E2;
	if (delta>0.0) {  // one real root, show in orange
		delta = sqrt(delta);
		t = (delta>q?pow(delta-q,_13):-pow(q-delta,_13))
            -(delta+q>0.0?pow(delta+q,_13):-pow(-delta-q,_13));
		t = clamp(t-a,0.,1.);
		E = (C2*t+C1)*t+C0, sd = dot(E,E);
		col = vec3(.9,.6,.2);
	}
	else {  // three real roots
        t = sqrt(-p), q = _13*acos(q/(p*t)), p = 2.0*t;
		// the ever-existing real root, show in purple
		t = clamp(p*cos(q)-a,0.,1.);
		E = (C2*t+C1)*t+C0, sd = dot(E,E);
		col = vec3(.8,.4,.6);
		// show in blue
		t = clamp(-p*cos(q-PI_3)-a,0.,1.);
		E = (C2*t+C1)*t+C0, E2 = dot(E,E);
		if (E2<sd) sd = E2, col = vec3(.3,.6,.9);
		// show in green, this color doesn't show up
        // which means the following lines are unnecessary
		t = clamp(-p*cos(q+PI_3)-a,0.,1.);
		E = (C2*t+C1)*t+C0, E2 = dot(E,E);
		if (E2<sd) sd = E2, col = vec3(.4,.7,.6);
	}
	return sqrt(sd);
}


float hash(float x){  // [-1,1)
    return fract(sin(12.9898*x+12.87)*43758.5453)*2.0-1.0;
}
float noise(float x, float d){
    return mix(hash(floor(x)+d),hash(ceil(x)+d),fract(x));
}
void mainImage(out vec4 Col, in vec2 Pos) {
    float a = noise(iTime,0.5);
    vec2 A = vec2(sin(a),cos(a))*noise(iTime,0.0);
    a = noise(iTime,1.5);
    vec2 B = vec2(sin(a),cos(a))*noise(iTime,1.0);
    a = noise(iTime,2.5);
    vec2 C = vec2(sin(a),cos(a))*noise(iTime,2.0);

    vec2 p = 4.0*(Pos-0.5*iResolution.xy)/length(iResolution.xy);
    float d = SDBezier2(A,B,C,p);
	col *= 1.0-exp(-3.0*abs(d));
	col *= 0.8+0.2*cos(150.0*d);
	col = mix(col,vec3(1.0),1.0-smoothstep(0.0,0.02,abs(1.2*d)));
    Col = vec4(col,1.0);
}

#define AA 3

#define EPSILON 1e-3

#define MAX_STEP 1024
#define MAX_DIST 200.0

#define Unit 2000.0


float map(vec3 p) {
    float sd=sd_regularstar(1.618,1.0,5,p.xy);
    sd = opExtrusion(sd,2.0,p+vec3(0,0,0));
    return max(abs(abs(sd) - 0.3) - 0.1, p.z - 0.5);
}

vec3 calcGrad(vec3 p) {
	float k_111 = map(vec3(p.x + EPSILON, p.y + EPSILON, p.z + EPSILON));
	float k_100 = map(vec3(p.x + EPSILON, p.y - EPSILON, p.z - EPSILON));
	float k_010 = map(vec3(p.x - EPSILON, p.y + EPSILON, p.z - EPSILON));
	float k_001 = map(vec3(p.x - EPSILON, p.y - EPSILON, p.z + EPSILON));
	vec3 n = vec3(k_111 + k_100 - k_010 - k_001, k_111 - k_100 + k_010 - k_001, k_111 - k_100 - k_010 + k_001);
	return n / (4.0 * EPSILON);
}

vec3 castRay(vec3 p, vec3 d, vec3 light) {
    // cast ray
    float t = 100.0 * EPSILON, dt;
	vec3 P = p + t * d;
	if (map(p) < 0.0) return vec3(0.0, 0.0, 0.0);
	for (int i = 0; i < MAX_STEP; i++) {
		dt = map(p + t * d);
		t += dt;
        if (dt < -EPSILON) return vec3(1.0,0.0,0.0);
		if (dt < EPSILON) break;
		if (dt > MAX_DIST) {
            vec3 col = vec3(1.0,1.0,1.0);
			t = max(dot(d, light), 0.0);
			return t*t*col;
		}
	}
	P = p + t * d;
    vec3 n = normalize(calcGrad(P));
    if (dot(n, d) > 0.0) n = -n;

    // shadow
    d -= 2.0 * dot(d, n)*n;
	float r = 1.0;
	t = 0.01;
	for (int i = 0; i < MAX_STEP; i++) {
		dt = map(P + t * light);
		r = min(r, 15.0 * dt / t);
		if (r < 0.001 || t > 2.0) break;
		t += clamp(dt, 1.0, 2.0)*clamp(length(p) / Unit, 0.002, 0.2);
	}
	r = clamp(r,0.0,1.0);

    // final result
    float dif = clamp(dot(n, light), 0.0, 1.0);
    return (0.9*dif*r + 0.2*pow(max(dot(d,light),0.0),4.0) + 0.1)*vec3(0.9,0.8,0.8);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float h = 4.0*(cos(0.4*iTime)+1.0)+3.0;
    float r = sqrt(150.0-h*h) + 1.0*(cos(iTime)+1.0) + 8.0;
    vec3 pos = vec3(r*cos(iTime), r*sin(iTime), h);
    vec3 ctr = vec3(0.0,0.0,0.0);
    vec3 dir = ctr-pos;

    float rz=atan(dir.x,-dir.y), rx=atan(length(dir.xy),dir.z);
    mat3 M=mat3(cos(rz),sin(rz),0,-sin(rz),cos(rz),0,0,0,1)*mat3(1,0,0,0,cos(rx),sin(rx),0,-sin(rx),cos(rx));

    vec3 light = normalize(vec3(-0.3, 0.1, 1));

    vec3 col = vec3(0.0,0.0,0.0);
    for (int i=0;i<AA;i++) for (int j=0;j<AA;j++) {
        vec3 d = M*vec3(0.5*iResolution.x-(fragCoord.x+float(i)/float(AA)),-0.5*iResolution.y+(fragCoord.y+float(j)/float(AA)),Unit);
        col += castRay(pos,normalize(d),light);
    }
    col/=float(AA*AA);

    col.x=pow(col.x,0.75),col.y=pow(col.y,0.75),col.z=pow(col.z,0.75);

    fragColor = vec4(col,1.0);
}

#define PI 3.14159265358979

float det(vec2 a, vec2 b){
    return a.x*b.y-b.x*a.y;
}

float sd_circle(float r, in vec2 p) {
	return length(p) - r;
}

float sd_segment(vec2 v1, vec2 v2, in vec2 p) {
	float t = dot(v2 - v1, p - v1);
	if (t < 0.0) return length(p - v1);
	if (t > dot(v2-v1, v2-v1)) return length(p - v2);
	return abs(det(normalize(v2 - v1), p - v1));
}

float sd_box(vec2 Min, vec2 Max, in vec2 p) {	// order doesn't matter
	if (p.x > Min.x == p.x < Max.x) {
		if (p.y > Min.y == p.y < Max.y) {
			return -min(min(abs(p.x - Min.x), abs(p.x - Max.x)), min(abs(p.y - Min.y), abs(p.y - Max.y)));
		}
		return min(abs(p.y - Min.y), abs(p.y - Max.y));
	}
	else if (p.y > Min.y == p.y < Max.y) return min(abs(p.x - Min.x), abs(p.x - Max.x));
	return min(min(length(p - Min), length(p - Max)), min(length(p - vec2(Min.x, Max.y)), length(p - vec2(Max.x, Min.y))));
}

float sd_triangle(vec2 a, vec2 b, vec2 c, in vec2 p) {
	a -= p, b -= p, c -= p;
	if ((int(det(a, b) > 0.0) + int(det(b, c) > 0.0) + int(det(c, a) > 0.0)) % 3 != 0) {		// outside
		float t, m, d = 1e+30, td;
		t = dot(a - b, a), m = dot(b-a,b-a); if (t > 0.0 && t < m) d = abs(det(a, b - a)) / sqrt(m);
		t = dot(b - c, b), m = dot(c-b,c-b); if (t > 0.0 && t < m) { td = abs(det(b, c - b)) / sqrt(m); if (td < d) d = td; }
		t = dot(c - a, c), m = dot(a-c,a-c); if (t > 0.0 && t < m) { td = abs(det(c, c - a)) / sqrt(m); if (td < d) d = td; }
		td = length(a); if (td < d) d = td;
		td = length(b); if (td < d) d = td;
		td = length(c); if (td < d) d = td;
		return d;
	}
	else {	// inside
		float d = abs(det(a, normalize(b - a)));
		float td = abs(det(b, normalize(c - b))); if (td < d) d = td;
		td = abs(det(c, normalize(a - c))); if (td < d) d = td;
		return -d;
	}
}

float sd_regularpolygon(float r, int n, in vec2 P) {	// first vertex at (0,r)
	float ang = 2.0 * PI / float(n);
	float rang = atan(P.x,P.y);
	float a = rang - ang * float(int(rang / ang)); if (a < 0.0) a += ang;
	vec2 p = length(P) * vec2(cos(a), sin(a)) - vec2(r, 0.0), e = r * vec2(cos(ang) - 1.0, sin(ang));
	a = dot(p, e);
	if (a < 0.0) return length(p); if (a > dot(e,e)) return length(p - e);
	return det(p, normalize(e));
}

float sd_regularstar(float R, float r, int N, in vec2 P) {	// 0<r<R, first vertex at (0,R)
	float ang = 2.0 * PI / float(N);
	float rang = atan(P.x,P.y);
	float a = rang - ang * float(int(rang / ang)); if (a < 0.0) a += ang;
	if (a > 0.5*ang) a = ang - a; ang /= 2.0;
	vec2 p = length(P) * vec2(cos(a), sin(a)) - vec2(R, 0), e = r * vec2(cos(ang), sin(ang)) - vec2(R, 0.0);
	a = dot(p, e);
	if (a < 0.0) return length(p);
	if (a > dot(e,e)) return length(P) < r ? -length(p - e) : length(p - e);
	return det(p, normalize(e));
}

float sd_sector(float r, float ang, in vec2 p) {	// centered at origin, range [-ang, ang], 0<ang<π
	float a = atan(p.y / p.x); if (p.x < 0.0) a += p.y > 0.0 ? PI : -PI; a = abs(a) - ang;
	float sd = length(p);
	if (a < 0.0) {
		if (sd > r) return sd - r;
		if (a > -PI / 2.0) sd = max(sd - r, sd*sin(a));
		else sd = max(sd - r, -sd);
		return sd;
	}
	if (a > PI / 2.0) return sd;
	if (sd*cos(a) < r) return sd * sin(a);
	return sqrt(sd*sd + r * r - 2.0 * r*sd*cos(a));
}

float sd_arc(float r, float ang, in vec2 p) {	// centered at origin, range [-ang, ang], 0<ang<π
	float a = atan(p.y / p.x); if (p.x < 0.0) a += p.y > 0.0 ? PI : -PI; a = abs(a) - ang;
	float sd = length(p); if (a < 0.0) return abs(sd - r);
	return length(vec2(p.x - r * cos(ang), abs(p.y) - r * sin(ang)));
}

float sd_ellipse(float a, float b, in vec2 P) {	// a,b > 0
	vec2 p = abs(P);
    if (p.x > p.y) { p = p.yx; float c=a; a=b, b=c; }
	float l = b * b - a * a;
	float m = a * p.x / l, m2 = m * m;
	float n = b * p.y / l, n2 = n * n;
	float c = (m2 + n2 - 1.0) / 3.0, c3 = c * c * c;
	float q = c3 + m2 * n2*2.0;
	float d = c3 + m2 * n2;
	float g = m + m * n2;
	float co;
	if (d < 0.0) {
		float p = acos(q / c3) / 3.0;
		float s = cos(p);
		float t = sin(p)*sqrt(3.0);
		float rx = sqrt(-c * (s + t + 2.0) + m2);
		float ry = sqrt(-c * (s - t + 2.0) + m2);
		co = (ry + (l > 0.0 ? rx : -rx) + abs(g) / (rx*ry) - m) / 2.0;
	}
	else {
		float h = 2.0*m*n*sqrt(d);
		float s = q + h > 0.0 ? pow(q + h, 1.0 / 3.0) : -pow(-q - h, 1.0 / 3.0);
		float u = q > h ? pow(q - h, 1.0 / 3.0) : -pow(h - q, 1.0 / 3.0);
		float rx = -s - u - c * 4.0 + 2.0*m2;
		float ry = (s - u)*sqrt(3.0);
		float rm = sqrt(rx*rx + ry * ry);
		float p = ry / sqrt(rm - rx);
		co = (p + 2.0*g / rm - m) / 2.0;
	}
	vec2 closestPoint = vec2(a*co, b*sqrt(1.0 - co * co));
	float sd = length(closestPoint - p);
	if (p.y > closestPoint.y) return sd;
	return -sd;
}
float sd_ellipse_fast(float a, float b, in vec2 p) {
	float k0 = length(vec2(p.x / a, p.y / b));
	float k1 = length(vec2(p.x / (a*a), p.y / (b*b)));
	return k0 * (k0 - 1.0) / k1;
}





float SD_sphere(float r, in vec3 p) {
	return length(p) - r;
}

float SD_segment(vec3 v1, vec3 v2, in vec3 p) {
	float t = dot(v2-v1, p-v1);
	if (t < 0.0) return length(p - v1);
	if (t > dot(v2-v1, v2-v1)) return length(p-v2);
	return length(cross(normalize(v2-v1), p-v1));
}

float SD_box(vec3 b, in vec3 p) {
	vec3 d = vec3(abs(p.x), abs(p.y), abs(p.z)) - b;
	return length(max(d,0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

float SD_torus(float R, float r, in vec3 p) {		// xoy
	float d = length(p.xy) - R;
	return sqrt(d * d + p.z * p.z) - r;
}

float SD_cylinder_z(float r, float min_z, float max_z, in vec3 p) {	// perpendicular to xoy, min_z<max_z
	float d = length(p.xy) - r;
	if (p.z < max_z && p.z > min_z) return max(d, max(min_z - p.z, p.z - max_z));
	if (d < 0.0) return p.z > max_z ? p.z - max_z : min_z - p.z;
	return p.z > max_z ? sqrt(d*d + (p.z - max_z)*(p.z - max_z)) : sqrt(d*d + (p.z - min_z)*(p.z - min_z));
}
float SD_cylinder(vec3 v1, vec3 v2, float r, in vec3 p) {
	vec3 dr = v2 - v1; float h = length(dr); dr /= h;
	float t = dot(dr, p - v1), d = length(cross(p - v1, dr)) - r;
	if (t < 0.0) {
		if (d > 0.0) return sqrt(d * d + t * t);
		return -t;
	}
	if (t > h) {
		t -= h;
		if (d > 0.0) return sqrt(d * d + t * t);
		return t;
	}
	if (d > 0.0) return d;
	return max(max(-t, t - h), d);
}
float SD_cylinder(vec3 c, vec3 dir, float r, float h, in vec3 p) {	// dir unit vectors
	c -= p;
	float t = -dot(dir, c);
	float d = length(cross(c, dir)) - r;
	if (t < 0.0) {
		if (d > 0.0) return sqrt(d * d + t * t);
		return -t;
	}
	if (t > h) {
		t -= h;
		if (d > 0.0) return sqrt(d * d + t * t);
		return t;
	}
	if (d > 0.0) return d;
	return max(max(-t, t - h), d);
}

float SD_cone(vec3 c, vec3 dir, float r, float h, in vec3 p) {	// dir unit vector,  h,r > 0.0
	c -= p;
	float t = -dot(dir, c);
	float d = length(cross(c, dir));
	if (t > h) {
		if (d < r) return t - h;
		d -= r, t -= h; return sqrt(d * d + t * t);
	}
	if (t < 0.0) {
		if (r * d + h * t < 0.0) return sqrt(d * d + t * t);
		return (h * d - r * t) / sqrt(h * h + r * r);
	}
	if (r * (d - r) + h * (t - h) > 0.0) {
		d -= r, t -= h; return sqrt(d * d + t * t);
	}
	d = (h * d - r * t) / sqrt(h * h + r * r);
	if (d > 0.0) return d;
	return max(d, t - h);
}

float SD_ellipsoid(float xy, float z, in vec3 p) {	// exact, rotating ellipse
	return sd_ellipse(xy, z, vec2(length(p.xy), p.z));
}
float SD_ellipsoid(float a, float b, float c, in vec3 p) {	// not exact, problem occurs inside and far outside
	float k0 = length(vec3(p.x / a, p.y / b, p.z / c));
	float k1 = length(vec3(p.x / (a*a), p.y / (b*b), p.z / (c*c)));
	return k0 * (k0 - 1.0) / k1;
}

float SD_Bezier2(vec3 A, vec3 B, vec3 C, in vec3 P) {
	vec3 C2 = A - 2.0 * B + C, C1 = 2.0 * (B - A), C0 = A - P;
	float t = 2.0 * dot(C2, C2)
		, a = 3.0 * dot(C2, C1) / t, b = (2.0 * dot(C2, C0) + dot(C1, C1)) / t, c = dot(C1, C0) / t;
	float a2 = a * a, p = (-a2 / 3.0 + b) / 3.0, q = (a*a2 / 13.5 - a * b / 3.0 + c) / 2.0;
	float p3 = p * p*p, delta = q * q + p3;
#define E (C2*t + C1)*t + C0
	if (delta > 0.0) {
		delta = sqrt(delta);
		t = (delta > q ? pow(delta - q, 1.0/3.0) : -pow(q - delta, 1.0/3.0)) - (delta + q > 0.0 ? pow(delta + q, 1.0/3.0) : -pow(-delta - q, 1.0/3.0)) - a / 3.0;
		return t > 1.0 ? length(P - C) : t < 0.0 ? length(C0) : length(E);
	}
	else {
		q = acos(-q / sqrt(-p3)) / 3.0, p = 2.0 * sqrt(-p), a /= 3.0;
		t = p * cos(q) - a;
		float sd = t > 1.0 ? dot(P-C,P-C) : t < 0.0 ? dot(C0,C0) : dot(E,E);
		t = -p * cos(q + PI / 3.0) - a;
		sd = min(sd, t > 1.0 ? dot(P-C,P-C) : t < 0.0 ? dot(C0,C0) : dot(E,E));
		t = -p * cos(q - PI / 3.0) - a;
		sd = min(sd, t > 1.0 ? dot(P-C,P-C) : t < 0.0 ? dot(C0,C0) : dot(E,E));
		return sqrt(sd);
	}
#undef E
}


float s_union(in float a, in float b, in float k) {
	float h = 0.5 + 0.5*(b - a) / k;
	if (h < 0.0) return b; if (h > 1.0) return a;
	return (1.0 - h)*b + h * a - k * h * (1.0 - h);
}
float s_int(in float a, in float b, in float k) {
	float h = 0.5 - 0.5*(b - a) / k;
	if (h < 0.0) return b; if (h > 1.0) return a;
	return (1.0 - h)*b + h * a + k * h * (1.0 - h);
}
float s_sub(in float a, in float b, in float k) {
	float h = 0.5 - 0.5*(b + a) / k;
	if (h < 0.0) return a; if (h > 1.0) return -b;
	return (1.0 - h)*a - h * b + k * h * (1.0 - h);
}
float opExtrusion(float sd2, float h, in vec3 P) {	// exact, extrude toward z-axis
	if (P.z >= 0.0 && P.z <= h) return sd2 > 0.0 ? sd2 : max(sd2, max(-P.z, P.z - h));
	if (P.z > h) return sd2 > 0.0 ? sqrt(sd2*sd2 + (P.z - h)*(P.z - h)) : P.z - h;
	if (P.z < 0.0) return sd2 > 0.0 ? sqrt(sd2*sd2 + P.z*P.z) : -P.z;
}

#define AA 2
#define omega 1.0

#define MAX_STEP 1024
#define MAX_DIST 200.0
#define EPSILON 1e-4

#define PI 3.14159265358979

float sdCylinder(vec3 p, float th, float r){
    p.z=abs(p.z)-th;
    float d = length(p.xy)-r;
	if (p.z<0.0) return d>0.0?d:max(d,max(-th-p.z,p.z));
	return d<0.0?p.z:sqrt(d*d+p.z*p.z);
}

float sdCube(vec3 p, float r){
    p=abs(p)-r;
    return length(max(p,0.0)) + min(max(p.x,max(p.y,p.z)),0.0);
}

float sdDisk(vec3 p){
    return sdCylinder(p,0.06,1.2);
}

float sdAxis(vec3 p){
    return sdCylinder(p+vec3(0.0,0.0,0.2),1.3,0.07);
}

float sdDice(vec3 p){
    return sdCube(p-vec3(0.9,0.0,0.2),0.1);
}

float map(vec3 p){
    float sd=min(sdDisk(p),sdAxis(p));
    sd=min(sd,sdDice(p));
    return sd-0.02;
}

vec3 calcGrad(vec3 p){
	float k_111 = map(vec3(p.x + EPSILON, p.y + EPSILON, p.z + EPSILON));
	float k_100 = map(vec3(p.x + EPSILON, p.y - EPSILON, p.z - EPSILON));
	float k_010 = map(vec3(p.x - EPSILON, p.y + EPSILON, p.z - EPSILON));
	float k_001 = map(vec3(p.x - EPSILON, p.y - EPSILON, p.z + EPSILON));
	vec3 n = vec3(k_111 + k_100 - k_010 - k_001, k_111 - k_100 + k_010 - k_001, k_111 - k_100 - k_010 + k_001);
	return n / (4.0 * EPSILON);
}

float calcShadow(vec3 p, vec3 light){
    float t=0.01, dt;
    for (int i=0;i<64;i++){
        dt=map(p+t*light);
        if (dt>MAX_DIST) break;
        if (dt<EPSILON) return 0.5;
        t+=dt;
    }
    return 1.0;
}

vec3 castRay(vec3 p, vec3 d, vec3 light){
    float t=100.0*EPSILON, dt;
    for (int i=0;i<MAX_STEP;i++){
        dt = map(p+t*d);
        t+=dt;
        if (dt<EPSILON){
            p=p+t*d;
            vec3 n=calcGrad(p);
            float k=0.3+0.7*max(dot(n,light),0.0);
            k*=calcShadow(p,light);
            vec3 col;
            if (sdDice(p)-0.02<EPSILON) col=k*vec3(0.7,0.8,0.9);
            else if (sdAxis(p)-0.02<EPSILON) col=k*vec3(0.9,0.8,0.7);
            else if ((t=atan(p.y,p.x))>PI/3.0) col=k*vec3(0.8,1.0,1.0);
            else if (t<-PI/3.0) col=k*vec3(1.0,0.8,1.0);
            else col=k*vec3(1.0,1.0,0.8);
            return col;
        }
        if (t>MAX_DIST) return vec3(0);
        if (t<-EPSILON) return vec3(1,0,0);
    }
    return vec3(0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float Unit = 0.75*length(iResolution);
    float h = 2.0;
    float r = 5.0;
    float t = -omega*iTime;
    vec3 pos = vec3(r*cos(t), r*sin(t), h);
    vec3 dir = vec3(0.0,0.0,-0.1)-pos;

    float rz=atan(dir.x,-dir.y), rx=atan(length(dir.xy),dir.z);
    mat3 M=mat3(cos(rz),sin(rz),0,-sin(rz),cos(rz),0,0,0,1)*mat3(1,0,0,0,cos(rx),sin(rx),0,-sin(rx),cos(rx));

    vec3 l = normalize(vec3(0.4, 0.3, 0.7));
    l = vec3(l.x*cos(t)-l.y*sin(t),l.x*sin(t)+l.y*cos(t),l.z);

    vec3 col = vec3(0.0,0.0,0.0);
    for (int i=0;i<AA;i++) for (int j=0;j<AA;j++) {
        vec3 d = M*vec3(0.5*iResolution.x-(fragCoord.x+float(i)/float(AA)),-0.5*iResolution.y+(fragCoord.y+float(j)/float(AA)),Unit);
        col += castRay(pos,normalize(d),l);
    }
    col/=float(AA*AA);

    fragColor = vec4(col,1.0);
}

#define AA 2
#define Unit 0.5*length(iResolution.xy)

#define EPSILON 1e-5

struct Sphere{
    vec3 O;
    float r;
};
bool sphInt(in Sphere S, in vec3 P, in vec3 d, out float t, out vec3 n) {	// doesn't work when inside
	vec3 p = P - S.O; if (dot(p, d) >= 0.0) return false;
	vec3 k = cross(p, d); float rd2 = dot(k,k); if (rd2 >= S.r*S.r) return false;
	t = sqrt(dot(p,p) - rd2) - sqrt(S.r*S.r - rd2); if (t < EPSILON) return false;
	n = (p + t * d) / S.r; return true;
}
const Sphere sph1 = Sphere(vec3(-2.0,-2.0,1.0),1.0);
const Sphere sph2 = Sphere(vec3(3.0,-2.0,1.3),1.3);
const Sphere sph3 = Sphere(vec3(1.0,3.0,1.8),1.8);
const Sphere sph4 = Sphere(vec3(1.0,1.0,0.4),0.4);


vec3 traceRay(vec3 p, vec3 d, vec3 light){
    vec3 col=vec3(1.0), ecol;
    for (int i=0;i<64;i++){
    	float t, mt=1e+12; vec3 n, mn; bool r=false;
    	t=-p.z/d.z;
        if (t>EPSILON){
            mt=t, mn=vec3(0.0,0.0,1.0), r=true;
            vec3 q=p+t*d;
            ecol = ((int(floor(q.x))&1)==(int(floor(q.y))&1)) ? vec3(135,206,250)/256.0 : vec3(148,166,188)/256.0;
        }
    	if (sphInt(sph1,p,d,t,n) && t<mt) r=true, mt=t, mn=n, ecol=vec3(221,160,221)/256.0;
    	if (sphInt(sph2,p,d,t,n) && t<mt) r=true, mt=t, mn=n, ecol=vec3(173,216,230)/256.0;
    	if (sphInt(sph3,p,d,t,n) && t<mt) r=true, mt=t, mn=n, ecol=vec3(255,182,193)/256.0;
    	if (sphInt(sph4,p,d,t,n) && t<mt) r=true, mt=t, mn=n, ecol=vec3(244,164,96)/256.0;
        if (r) {
            p+=mt*d;
            d-=2.0*dot(mn,d)*mn;
            col*=ecol;
        }
        else {
            col *= vec3(max(dot(d,light),0.0));
            break;
        }
    }
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float h = 2.0*(cos(0.4*iTime)+2.0);
    float r = sqrt(40.0-h*h) + 0.5*(cos(iTime)+1.0) + 3.0;
    vec3 pos = 2.0*vec3(r*cos(iTime), r*sin(iTime), h);
    vec3 dir = vec3(0.0,0.0,1.0)-pos;

    float rz=atan(dir.x,-dir.y), rx=atan(length(dir.xy),dir.z);
    mat3 M=mat3(cos(rz),sin(rz),0,-sin(rz),cos(rz),0,0,0,1)*mat3(1,0,0,0,cos(rx),sin(rx),0,-sin(rx),cos(rx));

    vec3 light = normalize(vec3(0.0,0.0,1.0));

    vec3 col = vec3(0.0,0.0,0.0);
    for (int i=0;i<AA;i++) for (int j=0;j<AA;j++) {
        vec3 d = M*vec3(0.5*iResolution.x-(fragCoord.x+float(i)/float(AA)),-0.5*iResolution.y+(fragCoord.y+float(j)/float(AA)),Unit);
        col += traceRay(pos,normalize(d),light);
    }
    col/=float(AA*AA);

    fragColor = vec4(col,1.0);
}

#define AA 2
#define Unit 0.5*length(iResolution.xy)


// Modeling

#define MIN_D 1e-2
#define MAX_D 1e+3
#define RTMinD 1e-2
#define RTMaxD 1e+12

const float h=0.5, h1=0.51, w=1.618, s=10.0, wp=1.0;
const vec2 r = vec2(s,s*w);

float surf(vec3 p, out vec3 grad) {
    float m=length(p.xy), n=m-iTime;
    float r=h*sin(n);
    m=h*cos(n)/m;
    grad.x=p.x*m;
    grad.y=p.y*m;
    grad.z=1.0;
    //return (p.z+r)/sqrt(dot(grad,grad)+1.0);
    return 0.9*(p.z+r)/length(grad);
}
bool surfint(vec3 p, vec3 d, out float t, out vec3 n){
    if (d.z>0.0 && p.z>h1) return false;
    float sd;
    t=10.0*MIN_D;
    if (d.z<0.0&&p.z>h1) t=-(p.z-1.0)/d.z;
    for (int i=0;i<100;i++){
        sd=surf(p+t*d,n);
        if (sd<-MIN_D) return false;
        t+=sd;
        if (t>MAX_D) return false;
        if (t<MIN_D) break;
    }
    return true;
}

bool sphint(vec3 p, vec3 d, out float t, out vec3 n){
    p.z-=h1+1.0;
    if (dot(p, d) >= 0.0) return false;
	vec3 k = cross(p, d); float rd2 = dot(k,k); if (rd2 >= 1.0) return false;
	t = sqrt(dot(p,p) - rd2) - sqrt(1.0 - rd2); if (t < RTMinD) return false;
	n = p + t * d; return true;
}

