    t1=0.5/s*(q.x+q.y/w), n=vec3(t1,t1/w,-1.);
    t1=length(n.xy/vec2(1.,w)), t2=length(n.xy/vec2(1./w*w));
  	delta=t1*(t1-1.0)/t2/length(n.xy);
