// all sdf functions copy-pasted from iq

float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}
float sdEllipsoid( vec3 p, vec3 r ) {
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}
float sdCylinder(vec3 p, vec3 a, vec3 b, float r) {
  vec3  ba = b - a;
  vec3  pa = p - a;
  float baba = dot(ba,ba);
  float paba = dot(pa,ba);
  float x = length(pa*baba-ba*paba) - r*baba;
  float y = abs(paba-baba*0.5)-baba*0.5;
  float x2 = x*x;
  float y2 = y*y*baba;
  float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));
  return sign(d)*sqrt(abs(d))/baba;
}

float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}
float smax( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h);
}


mat3 axisAngle(vec3 a, float t) {  // actually returns its inverse/transpose
	a = normalize(a); float ct = cos(t), st = sin(t);
	return mat3(
		ct + a.x*a.x*(1.-ct), a.x*a.y*(1.-ct) - a.z*st, a.x*a.z*(1.-ct) + a.y*st,
		a.y*a.x*(1.-ct) + a.z*st, ct + a.y*a.y*(1.-ct), a.y*a.z*(1.-ct) - a.x*st,
		a.z*a.x*(1.-ct) - a.y*st, a.z*a.y*(1.-ct) + a.x*st, ct + a.z*a.z*(1.-ct)
	);
}


