// Forward Euler

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 xy = vec2(width,height)*fragCoord/iResolution.xy;

    vec4 sp = diffequ(iChannel0, time, xy, uniforms_call);
    float u = sp.x,
          ut = sp.y,
          utt = sp.z;

    ut += delta_t * utt;
    u += delta_t * ut;

    // clamp it to show independence from float buffer extension
    fragColor = clamp( vec4(u, 0.5+nm1*ut, 0,0), 0.,1.);
}

#define PI 3.1415927

// pass uniform variables to common
#define uniforms_params vec3 iResolution, vec4 iMouse, int iFrame
#define uniforms_call iResolution, iMouse, iFrame

// simulation dimension in meters
#define height (2.)
#define width (height*(iResolution.x/iResolution.y))
#define view_box (.5*vec3(width,height,1))

// time in seconds (2 buffers, 60 fps)
#define delta_t (1./(60.*2.))
#define time (float(iFrame)*(delta_t))

// speed of wave in m/s, large => unstable
// in reality: some number between 0.4 and 2.2
#define speed (0.3)

// sampling buffer, normalize between 0 and 1??
#define nm1 (.03/speed)
#define getU(xy) (texture(sampler,(xy)/vec2(width,height)).x)
#define getUt(xy) ((texture(sampler,(xy)/vec2(width,height)).y-.5)/nm1)

// numerical differentiation step in meters
#define nds vec2(height/iResolution.y,0)


// returns vec4(u, ∂u/∂t, ∂²u/∂t²)
// sampler wrap must be "clamp"
vec4 diffequ(sampler2D sampler, float t, vec2 xy, uniforms_params) {
    float u = getU(xy);
    float ut = getUt(xy);

    float ux1 = getU(xy+nds.xy);
    float ux0 = getU(xy-nds.xy);
    float uy1 = getU(xy+nds.yx);
    float uy0 = getU(xy-nds.yx);

    float uxx = (ux1+ux0-2.*u) / (nds.x*nds.x);
    float uyy = (uy1+uy0-2.*u) / (nds.x*nds.x);

    float v = speed;
    float utt = v*v*(uxx+uyy); // wave equation
    utt -= 0.1*ut;  // damping

    // mouse interaction
    if (iMouse.z > 0.) {
        vec2 pos = vec2(width,height)*iMouse.xy/iResolution.xy;
        u += 1.0*exp(-1000.*dot(xy-pos,xy-pos))*delta_t;
    }
    // initial state
    if (iFrame < 1) {
        vec2 pos = vec2(.4,.3)*vec2(width,height);
        u = .5 + .2*exp(-1000.*dot(xy-pos,xy-pos));
        ut = utt = 0.;
    }
    return vec4(u, ut, utt, 1);
}

// Forward Euler

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 xy = vec2(width,height)*fragCoord/iResolution.xy;

    vec4 sp = diffequ(iChannel0, time, xy, uniforms_call);
    float u = sp.x,
          ut = sp.y,
          utt = sp.z;

    ut += delta_t * utt;
    u += delta_t * ut;

    // clamp it to show independence from float buffer extension
    fragColor = clamp( vec4(u, 0.5+nm1*ut, 0,0), 0.,1.);
}

