vec3 Render_Main(float x, float y) {
    float m = x*x + y*y;
    float t = acos(cos(5.0*atan(x,-y)));
    float r = -0.032*log(exp(-0.03*exp(3.1*t)-2.7*t-6.8)+exp(0.46*abs(t)-25.2));
    float d = min(max(m-1.7,1.45-m), sqrt(m)-r);
    float sd = 1.0-d/length(vec2(dFdx(d),dFdy(d)));
    return clamp(sd, 0.0, 1.0) * vec3(0.98, 0.86, 0.02);
}

