// play around this
void calcAbsorb(in vec3 p, out vec3 emi, out vec3 tabs, out vec3 sabs, out float k, out float g) {
    p -= vec3(0, 0, 1);  // center of sphere
    emi = vec3(0.0);
    vec3 q = p;
    float a = 0.4*q.z;
    q.xy = mat2(cos(a), -sin(a), sin(a), cos(a)) * q.xy;
    vec3 c = mix(vec3(0.8,0.2,0.1), vec3(0.1,0.2,0.8), 0.5+0.5*q.z);
    for (float i=0.; i<4.; i++) {
        float d = 0.5+0.5*asin(sin(6.0*atan(q.y, q.x)));
        float r = length(q);
        if (d<0.05 && r<1.0) emi += 30.0*exp2(i)*c * max(vec3(-1.6)+vec3(2.)*r+vec3(0.2)*cos(20.*PI*r), vec3(0.));
        q *= 2.0;
        c = mix(c, vec3(0,1,0.5), 0.2);
    }
    tabs = vec3(0.0);
    sabs = vec3(1.0);
    k = 0.0;
    g = 0.5;
}

void calcScatter(in vec3 ro, inout vec3 rd,
        inout float mt, inout vec3 m_col, out vec3 m_emi, out int material, inout vec3 min_n) {
