            m_col *= sabs * exp(-tabs*dt);
            material = mat_none;
            min_n = vec3(0.0);
            return;
        }
        p *= dp;
        m_col *= exp(-tabs*dt);
        m_emi += m_col * emi * dt;
    }
    material = mat_refractive;
}


